graphql.query:
mutation Register($input: RegisterInput!) {
  register(input: $input) {
    token
    user {
      id
      email
      firstName
      lastName
      role
      restaurantId
      isActive
      createdAt
      updatedAt
    }
  }
}

mutation Login($input: LoginInput!) {
  login(input: $input) {
    token
    user {
      id
      email
      firstName
      lastName
      role
      restaurantId
      isActive
      createdAt
      updatedAt
    }
  }
}

query Me {
  me {
