graphql.query:
mutation UpdateRestaurant($id: ID!, $input: UpdateRestaurantInput!) {
  updateRestaurant(id: $id, input: $input) {
    id
    name
    description
    isActive
    updatedAt
  }
}

mutation DeleteRestaurant($id: ID!) {
  deleteRestaurant(id: $id)
}

query MenuItems($restaurantId: ID, $first: Int, $skip: Int) {
  menuItems(restaurantId: $restaurantId, first: $first, skip: $skip) {
    id
    name
    description
    price
    imageUrl
    isAvailable
    category
    restaurant {
      id
      name
      city
      location
    }
    createdAt
    updatedAt
  }
}

query MenuCategories($restaurantId: ID) {
  menuCategories(restaurantId: $restaurantId)
}

mutation CreateMenuItem($input: CreateMenuItemInput!) {
