query Orders($first: Int, $skip: Int) {
  orders(first: $first, skip: $skip) {
    id
    status
    totalAmount
    deliveryAddress
    phone
    specialInstructions
    user {
      id
      email
      firstName
      lastName
    }
    items {
      id
      quantity
      price
      notes
      menuItem {
        id
        name
        category
      }
    }
    payment {
      id
      amount
      status
      transactionId
    }
    createdAt
    updatedAt
  }
}

query Order($id: ID!) {
  order(id: $id) {
    id
    status
    totalAmount
    deliveryAddress
    phone
    specialInstructions
    user {
      id
      email
      firstName
      lastName
      role
    }
    items {
      id
      quantity
      price
      notes
      menuItem {
        id
        name
        description
        price
        category
        imageUrl
      }
    }
    payment {
      id
      amount
      status
      method {
        id
        type
        provider
        last4
      }
      transactionId
    }
    createdAt
    updatedAt
  }
}

mutation CreateOrder($input: CreateOrderInput!) {
  createOrder(input: $input) {
    id
